class Solution {
    public long[] handleQuery(int[] nums1, int[] nums2, int[][] queries) {
        SegmentTreeLazy st = new SegmentTreeLazy(nums1);
        int n = nums1.length;

        long sum = 0;
        for(int i: nums2) sum += i;

        int ones = st.query(0, n - 1);
        // System.out.println(" ones " + ones);
        long s = 0;
        int q = queries.length;
        List<Long> ll = new ArrayList<>();
        for(int i = 0; i < q; i++){
            int[] v = queries[i];
            if(v[0] == 1){
                int l = v[1], r = v[2];
                st.rangeUpdate(l, r);
            }else if(v[0] == 2){
                s += (long)v[1]*st.query(0, n - 1);
            }else{
                ll.add((sum + s));
            }
        }

        long[] arr = new long[ll.size()];
        for(int i = 0; i < arr.length; i++){
            arr[i] = ll.get(i);
        }
        return arr;
    }
}

        class SegmentTreeLazy{
        int[] seg, lazy;
        int n;
        int[] givenArr;
        int MAXN = 400000;
        SegmentTreeLazy(int[] nums){
            this.seg = new int[MAXN];
            this.lazy = new int[MAXN];
            this.givenArr = nums;
            this.n = givenArr.length;
            build(0, 0, n - 1);
        }

        void build(int index, int l, int r) {
            if (l == r) {
                seg[index] = givenArr[l];
                return;
            }
            int mid = (l + r) / 2;
            build(2*index + 1, l, mid);
            build(2*index + 2, mid + 1, r);
            seg[index] = seg[2*index + 1] + seg[2*index + 2];
        }

        // HINT: here we are adding.., can be changed acc. to question
        void rangeUpdate(int ind, int low, int high, int l, int r){
            if(lazy[ind] != 0){
                seg[ind] = (high - low + 1 - seg[ind]);
                if(low != high){
                    lazy[2*ind + 1] = 1 - lazy[2*ind + 1];
                    lazy[2*ind + 2] = 1 - lazy[2*ind + 2];
                }

                lazy[ind] = 0;
            }

            if(r < low || l > high) return ;
            if(low >= l && high <= r){
                seg[ind] = (high - low + 1 - seg[ind]);
                // if this is not leaf node
                if(low != high){
                    lazy[2*ind + 1] = 1 - lazy[2*ind + 1];
                    lazy[2*ind + 2] = 1 - lazy[2*ind + 2];
                }
                return;
            }


            int mid = (low + high)/2;
            rangeUpdate(2*ind + 1, low, mid, l, r);
            rangeUpdate(2*ind + 2, mid + 1, high, l, r);
            seg[ind] = seg[2*ind + 1] + seg[2*ind + 2];
            return;
        }
        // range update
        void rangeUpdate(int l, int r){
            // NOTE: this " val " can be added, subtracted, xor etc.
            // val = adding val to range [l, r]
            rangeUpdate(0, 0, n - 1, l, r);
        }

        int query(int ind, int low, int high, int l, int r){
            if(lazy[ind] != 0){
                seg[ind] = (high - low + 1 - seg[ind]);
                if(low != high){
                    lazy[2*ind + 1] = 1 - lazy[2*ind + 1];
                    lazy[2*ind + 2] = 1 - lazy[2*ind + 2];
                }
                lazy[ind] = 0;
            }

            if(r < low || high < l) return 0;

            // total - overlapping
            if(low >= l && high <= r){
                return seg[ind];
            }

            int mid = (low + high)/2;
            int le = query(2*ind + 1, low, mid, l, r);
            int re = query(2*ind + 2, mid + 1, high, l, r);
            return le + re;
        }
        int query(int l, int r){
            return query(0, 0, n - 1, l, r);
        }
    }
