# Intuition
 Flipping bits in a range suggests segment tree?? Isn't it? But, see we have to do range update. How do we do that?

# Approach
  We use segment tree with lazy propagation.

# Complexity
- Time complexity:
<!-- Add your time complexity here, e.g. $$O(n)$$ -->  
    O(nlogn)
- Space complexity:
<!-- Add your space complexity here, e.g. $$O(n)$$ -->
      O(n)

# Code
```
#define ll long long 
class SegmentTree{
  ll *val, *lazy;
  ll n;
public:

SegmentTree(){

}
  SegmentTree(ll size){
      n=size;
      val=new ll[4*size+1];
      lazy=new ll[4*size+1];
      for(ll i=0;i<4*size+1;i++)
      lazy[i]=val[i]=0;
  }

  void build(vector<int>&a,ll si,ll l,ll r){
     if(l>r)
     return;

     if(l==r){
         val[si]=a[l];
         return;
     }

     ll mid=(l+r)/2;
     build(a,2*si+1,l,mid);
     build(a,2*si+2,mid+1,r);
     val[si]=val[2*si+1] + val[2*si+2];
  }

  void apply(ll si,ll ss,ll se){
    //    update the value of si
      val[si]=se-ss+1-val[si];
      lazy[si]^=1;
  }

  void push(ll si,ll ss,ll se){
      if(lazy[si]&&ss!=se){
          lazy[si]^=1;
          ll mid=(ss+se)/2;
          apply(2*si+1,ss,mid);
          apply(2*si+2,mid+1,se);
      }
  }

  void update(ll si,ll ss,ll se,ll l,ll r){
     if(l>se||r<ss)
     return;

     if(ss>=l&&se<=r){
         apply(si,ss,se);
         return;
     }

     push(si,ss,se);
     ll mid=(ss+se)/2;
     update(2*si+1,ss,mid,l,r);
     update(2*si+2,mid+1,se,l,r);
     val[si]=val[2*si+1] + val[2*si+2];
  }

  ll get(ll si,ll ss,ll se,ll l,ll r){
     if(l>se||r<ss)
     return 0;

     if(ss>=l&&se<=r){
       return val[si];
     }

     push(si,ss,se);
     ll mid=(ss+se)/2;
     return get(2*si+1,ss,mid,l,r) + get(2*si+2,mid+1,se,l,r); 
  }
};

class Solution {
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        vector<long long>ans;
        int n=nums1.size();

        SegmentTree sg(n+1);

        sg.build(nums1,0,0,n-1);
        ll sum=0;
        for(auto e:nums2){
            sum+=e;
        }

        for(auto e:queries){
            if(e[0]==1){
                sg.update(0,0,n-1,e[1],e[2]);
            }
            else if(e[0]==2){
                sum+=e[1]*1LL*sg.get(0,0,n-1,0,n-1);
            }
            else
            {
                ans.push_back(sum);
            }
        }


        return ans;
    }
};
```

