# Intuition
The sum is for the entire array, so the only problem is queries of type 1 and 2, 1 updates the range, 2 asks for the sum of all 1s in the array. We can maintain the sum using blocks of size sqrt(n). 

# Approach
update(l, r) = update(r) & update(l-1), the elements at the beginning get flipped twice.

# Complexity
- Time complexity:
O(n*sqrt(n))

- Space complexity:
O(n)

# Code
```
class Solution {
private:
    static const int BLK = 320;
    static const int LIM = 1e5+1;
    int blk_sum[BLK], is_flip[BLK], orig[LIM], n;
    void compute_block_sum(int blk_no) {
        int sum = 0;
        for(int i=blk_no*BLK;i<min(blk_no*BLK+BLK, n);i++) {
            sum += orig[i];
        }
        blk_sum[blk_no] = sum;
    }
    void apply_flip(int blk_no) {
        if(!is_flip[blk_no]) {
            return;
        }
        for(int i=blk_no*BLK;i<min(blk_no*BLK+BLK, n);i++) {
            orig[i] ^= 1;
        }
        compute_block_sum(blk_no);
        is_flip[blk_no] = 0;
    }
    void update(int r) {
        int rb = r/BLK;
        apply_flip(rb);
        for(int i=0;i<rb;i++) {
            is_flip[i] ^= 1;
        }
        for(int i=rb*BLK;i<=r;i++) {
            orig[i] ^= 1;
        }
        compute_block_sum(rb);
    }
    int query_full() {
        int rb = (n-1)/BLK, sum = 0;
        apply_flip(rb);
        for(int i=0;i<rb;i++) {
            if(is_flip[i]) {
                sum += BLK - blk_sum[i];
            } else {
                sum += blk_sum[i];
            }
        }
        for(int i=rb*BLK;i<n;i++) {
            sum += orig[i];
        }
        return sum;
    }
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        n = nums2.size();
        memset(is_flip, 0, sizeof(is_flip));
        memset(blk_sum, 0, sizeof(blk_sum));
        vector<long long> ans_vec;
        for(int i=0;i<n;i++) {
            orig[i] = nums1[i];
        }
        for(int i=0;i<n/BLK;i++) {
            compute_block_sum(i);
        }

        long long ans = accumulate(nums2.begin(), nums2.end(), 0LL);
        for(vector<int>& query:queries) {
            int type = query[0], l = query[1], r = query[2];
            if(type==1) {
                if(l!=0) {
                    update(l-1);
                }
                update(r);
            } else if(type==2) {
                ans += 1LL*query_full()*l;
            } else if(type==3) {
                ans_vec.push_back(ans);
            }
        }
        return ans_vec;
    }
};
