class Solution {
    public long[] handleQuery(int[] nums1, int[] nums2, int[][] queries) {
        int n = 0;
        for (int [] query : queries) if (query[0] == 3) n++;
        long sum = 0;
        for (int num : nums2) sum += num;
        for (int i = 0; i < nums1.length; i++) if (nums1[i] == 0) nums1[i] = -1;
        long[] ans = new long[n];
        SegmentTree tree = new SegmentTree(nums1);
        int k = 0;
        for (int[] q : queries) {
            if (q[0] == 1) tree.flip(query[1], query[2]);
            else if (q[1] == 2) sum = sum + query[1] * ((tree.query(0, tree.n-1) + tree.n)/2);
            else ans[k++] = sum;
        }

        return ans;
    }

    class SegmentTree {
        long[] tree;
        long[] lazy;
        int n;

        public SegmentTree(int[] nums) {
            n = nums.length;
            tree = new long[4 * n];
            lazy = new long[4 * n];
            Arrays.fill(lazy, 1L);
            build(0, 0, n-1, nums);
        }

        // build the segment tree, looks fine
        public void build(int node, int l, int r, int[] nums) {
            if (l == r) {
                tree[node] = (long)nums[l];
                return;
            }

            int mid = (l + r)/2;

            build(node * 2 + 1, l, mid, nums);
            build(node * 2 + 2, mid+1, r, nums);

            tree[node] = tree[node*2+1] + tree[node*2+2];
        }

        public void flip(int l, int r) {
            flip(0, 0, n-1, l, r);
        }

        public void flip(int node, int start, int end, int l, int r) {
            // IMPORTANT: Before ANYTHING, update the node if its not up to date
            // For some reason I got WA when checking if out of bounds before updating
            if (lazy[node] != 1) {

                tree[node] *= lazy[node];
                // step 2: merge changes down to children if its not a leaf node
                if (start != end) {
                    lazy[node * 2 + 1] *= lazy[node];
                    lazy[node * 2 + 2] *= lazy[node];
                }

                // step 3: set the lazy update of this node to 1
                lazy[node] = 1;
            }

            if (end < l || start > r) return;
            if (end <= r && start >= l) {

                tree[node] *= -1;
                if (start != end) {
                    lazy[node * 2 + 1] *= -1;
                    lazy[node * 2 + 2] *= -1;
                }

                lazy[node] = 1;
                return;
            }

            int mid = (end + start)/2;
            flip(node * 2 + 1, start, mid, l, r);
            flip(node * 2 + 2, mid + 1, end, l, r);
            tree[node] = tree[node * 2 + 1] + tree[node * 2 + 2];
        }

        public long query(int l, int r) {
            return query(0, 0, n-1, l, r);
        }

        public long query(int node, int start, int end, int l, int r) {
            // step 1: if this node is not updated, update this node
            if (lazy[node] != 1) {

                tree[node] *= lazy[node];
                // step 2: merge changes down to children if its not a leaf node
                if (start != end) {
                    lazy[node * 2 + 1] *= -1;
                    lazy[node * 2 + 2] *= -1;
                }

                // step 3: set the lazy update of this node to 1
                lazy[node] = 1;
            }

            if (end < l || start > r) return 0;
            if (end <= r && start >= l) return tree[node];
            int mid = (end + start) / 2;
            return query(node * 2 + 1, start, mid, l, r) + query(node * 2 + 2, mid + 1, end, l, r);
        }
    }
}
