class STree{
public:
    vector<long long>seg;
    vector<long long>lazy;
    STree(int n){
        seg.resize(4*n+1);
        lazy.resize(4*n+1,0);
        return;
    }

    void build(vector<int>&nums,int ind,int low,int high){
        if(low==high){
            seg[ind]=nums[low];
            return;
        }
        int mid=(low+high)/2;
        build(nums,2*ind+1,low,mid);
        build(nums,2*ind+2,mid+1,high);
        seg[ind]=seg[2*ind+1]+seg[2*ind+2];
    }

    void update(int ind,int low,int high,int l,int r){
        if(lazy[ind]!=0){
            seg[ind]=(high-low+1)-seg[ind];
            if(low!=high){
                lazy[2*ind+1]=!lazy[2*ind+1];
                lazy[2*ind+2]=!lazy[2*ind+2];
            }
            lazy[ind]=0;
        }
        if(low>r or high<l){ return;}
        if(l<=low and high<=r){
            seg[ind]=(high-low+1)-seg[ind];
            if(low!=high){
                lazy[2*ind+1]=!lazy[2*ind+1];
                lazy[2*ind+2]=!lazy[2*ind+2];
            }
            return;
        }  
        int mid=(low+high)>>1;
        update(2*ind+1,low,mid,l,r);
        update(2*ind+2,mid+1,high,l,r);
        seg[ind]=seg[2*ind+1]+seg[2*ind+2];
    }
};

class Solution {
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        long long sum=0;
        int n=nums1.size();
        STree st(n+1);
        st.build(nums1,0,0,n-1);
        for(int i=0;i<nums2.size();i++){
            sum+=nums2[i];
        }
        vector<long long>res;
        for(auto query:queries){
            int type=query[0];
            if(type==1){
                st.update(0,0,n-1,query[1],query[2]);
            }
            else if(type==2){
                sum+=st.seg[0]*query[1];
            }
            else{
                res.push_back(sum);
            }
        }
        return res;
    }
};
