            return segTree[c][1] - segTree[c][0] + 1 - segTree[c][2];
        }
        return segTree[c][2];
    }
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        vector<long long> ans;
        long long sum = 0;
        for(int x: nums2) sum += x;
        int t = 1;
        while(t < nums1.size()) t *= 2;
        int s = t-1;
        t += s;
        vector<vector<int> > segTree(t, vector<int>());
        for(int i = 0; i < nums1.size(); i++){
            segTree[s + i] = {i, i, nums1[i], 0};
        }
        for(int i = nums1.size(); i <= s; i++){
            segTree[i + s] = {i, i, 0, 0};
        }
        for(int i = s - 1; i >= 0; i--){
            segTree[i] = {segTree[2*i + 1][0], segTree[2*i + 2][1], segTree[2*i + 1][2] + segTree[2*i + 2][2], 0};
        }
        for(vector<int> x: queries){
            if(x[0] == 3) ans.push_back(sum);
            else if(x[0] == 2) {
                if(segTree[0][3] == 1){
                    sum += (long)x[1] * (nums1.size() - segTree[0][2]);
                }
                else{
                    sum += (long)x[1] * segTree[0][2];
                }
            }
            else {
                flip(segTree, x[1], x[2], 0);
            }
        }
        return ans;
    }
};
