class Solution {
    int flip(vector<vector<int> > &segTree, int i, int j, int c){
        if(j >= i) {
            if(segTree[c][0] == i && segTree[c][1] == j){
                segTree[c][3] ^= 1;
                segTree[c][2] = segTree[c][2] = segTree[c][1] - segTree[c][0] + 1 - segTree[c][2];
            }
            else{
                int l = flip(segTree, i, min(j, segTree[2*c + 1][1]), 2*c + 1);
                int r = flip(segTree, max(i, segTree[2*c + 2][0]), j, 2*c + 2);
                if(segTree[c][3] == 1) segTree[c][2] = segTree[c][1] - segTree[c][0] + 1 - (l + r);
                else segTree[c][2] = l + r;
            }
        }
        return segTree[c][2];
    }
