class Solution {
public:

struct Node{
	int sum,lazy;
	Node(){
		sum = 0, lazy =0;
	}
};

Node t[4*100100];
int arr[100100];

Node merge(Node a, Node b){
	Node ans;
	ans.sum = a.sum+b.sum;
	return ans;
}

void push(int id, int l, int r){
	if(!t[id].lazy) return;
	if(t[id].lazy){
		t[id].sum = (r-l+1)-t[id].sum;
		if(l!=r){
			t[id<<1].lazy = 1-t[id<<1].lazy;
			t[id<<1|1].lazy=1-t[id<<1|1].lazy;
		}
		t[id].lazy = 0;
	}
}

void updateP(int id, int l, int r, int idx, int val){
	if(l>r || l>idx || r<idx) return;
	if(l==r){
		t[id].sum=val;
		t[id].lazy=0;
		return;
	}
	int mid = (l+r)>>1;
	updateP(id<<1,l,mid,idx,val);
	updateP(id<<1|1,mid+1,r,idx,val);
	t[id]=merge(t[id<<1],t[id<<1|1]);
}

void updateR(int id, int l, int r, int lq, int rq){
	push(id,l,r);
	if(lq>r || rq<l){
		return;
	}
	if(lq<=l && r<=rq){
		t[id].lazy = 1;
		push(id,l,r);
		return;
	}
	int mid = (l+r)>>1;
	updateR(id<<1,l,mid,lq,rq);
	updateR(id<<1|1,mid+1,r,lq,rq);
	t[id]=merge(t[id<<1],t[id<<1|1]);

}

Node query(int id, int l, int r, int lq, int rq){
	push(id,l,r);
	if(lq>r || rq<l) return Node();
	if(lq<=l && r<=rq){
		return t[id];
	}
	int mid = (l+r)>>1;
	return merge(query(id<<1,l,mid,lq,rq),query(id<<1|1,mid+1,r,lq,rq));
}

    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        int n = nums1.size();
        long long sum = 0;
        for(auto x: nums2){
            sum += x;
        }
        for(int i=0; i<n; i++){
            updateP(1,0,n-1,i,nums1[i]);
        }
        vector<long long> ans;
        for(auto x: queries){
            if(x[0]==1){
                updateR(1,0,n-1,x[1],x[2]);
            }else if(x[0]==2){
                Node t = query(1,0,n-1,0,n-1);
                sum += (1LL*t.sum*x[1]);
            }else{
                ans.push_back(sum);
            }
        }
        return ans;
    }
};
