struct seg_tree {
    int n;
    vector<int> lazy;
    vector<int> tree;
    seg_tree(vector<int>& input) {
        this->n = input.size();
        this->tree = vector<int>(4 * n, 0);
        this->lazy = vector<int>(4 * n, 0);
        build(1, 0, n - 1, input);
    }

    void push(int index, int left, int right) {
        tree[index] = right - left + 1 - tree[index];
        if (left != right) {
            lazy[index * 2] = lazy[index * 2] == 1 ? 0 : 1;
            lazy[index * 2 + 1] = lazy[index * 2 + 1] == 1 ? 0 : 1;
        }
        lazy[index] = 0;
    }

    void build(int index, int left, int right, vector<int>& input) {
        if (left == right) {
            tree[index] = input[left];
        } else {
            int mid = (right - left) / 2 + left;
            build(index * 2, left, mid, input);
            build(index * 2 + 1, mid + 1, right, input);
            tree[index] = tree[index * 2] + tree[index * 2 + 1];
        }
    }

    void update(int index, int left, int right, int l, int r) {
        if (lazy[index]) push(index, left, right);
        if (l > r) return;
        if (left >= l && right <= r) {
            tree[index] = right - left + 1 - tree[index];
            if (left != right) { // not leaf node
                lazy[index * 2] = lazy[index * 2] == 1 ? 0 : 1;
                lazy[index * 2 + 1] = lazy[index * 2 + 1] == 1 ? 0 : 1;
            }
            return;
        }
        int mid = (right - left) / 2 + left;
        update(index * 2, left, mid, l, min(r, mid));
        update(index * 2 + 1, mid + 1, right, max(l, mid + 1), r);
        tree[index] = tree[index * 2] + tree[index * 2 + 1];

    }

    int query(int index, int left, int right, int l, int r) {
        if (lazy[index]) push(index, left, right);
        if (l > r) return 0;
        if (left == l && right == r) return tree[index];
        int mid = (right - left) / 2 + left;
        int a = query(index * 2, left, mid, l, min(r, mid)), b = query(index * 2 + 1, mid + 1, right, max(l, mid + 1), r);
        tree[index] = a + b;
        return tree[index];
    }

};

class Solution {
public:

    #define ll long long

    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        int n = nums2.size();
        ll a = accumulate(nums2.begin(), nums2.end(), 0LL);
        seg_tree st(nums1);
        vector<ll> ans;
        for (auto& q : queries) {
            if (q[0] == 1) {
                st.update(1, 0, n - 1, q[1], q[2]);
            } else if (q[0] == 2) {
                a += (0LL + st.query(1, 0, n - 1, 0, n - 1)) * q[1];
            } else {
                ans.push_back(a);
            }
        }

        return ans;

    }
};


