const int N = 1e5;
#define ll long long 
class Solution {
public:
    vector<ll> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries)
    {
        vector<ll> ans;
        bitset<N> bset(0);
        ll sum = 0;
        bitset<N> binit(0);
        binit.flip();

        int n = nums2.size();
        for(int i=0; i<n;i++)
        {
            sum += nums2[i];
            if(nums1[i]==1)
                bset[i] = 1;
        }

        for(auto q : queries)
        {
            if(q[0]==1)
            {
                int size = q[2]-q[1]+1; //1
                bitset<N> btemp(binit<<size); //1 1 1<<1 --> 1 1 0
                btemp = ~btemp; // 0 0 1
                btemp <<= q[1]; // 0 1 0
                bset ^= btemp; // 101^010 = 1 1 1
            }
            else if(q[0]==2)
            {
                sum += q[1]*bset.count();// it will count number of ones present multiply by p ;
            }
            else
            {
                ans.push_back(sum);
            }
        }
        return ans;
    }
};
