class Solution {
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        const int n = nums1.size(), size = sqrt(n + 1.), m = (n + size - 1) / size;
        vector<int> s(m);
        vector<bool> rev(m);
        vector<long long> r;
        long long total = 0, v = 0;
        for (int i = 0; i < n; ++i) {
            s[i / size] += nums1[i];
            total += nums1[i];
            v += nums2[i];
        }
        for (const auto & q : queries) {
            if (q[0] == 1) {
                int x = q[1], y = q[2];
                while (x % size && x <= y) {
                    const int id = x / size;
                    if (nums1[x] == 1) {
                        --s[id];
                        if (rev[id]) {
                            ++total;
                        } else {
                            --total;
                        }
                    } else {
                        ++s[id];
                        if (rev[id]) {
                            --total;
                        } else {
                            ++total;
                        }
                    }
                    nums1[x++] ^= 1;  
                }
                while (y - x >= size - 1) {
                    const int id = x / size;
                    total -= rev[id] ? (size - s[id]) : s[id];
                    x += size;
                    rev[id] = !rev[id];
                    total += rev[id] ? (size - s[id]) : s[id];
                }
                while (x <= y) {
                    const int id = x / size;
                    if (nums1[x] == 1) {
                        --s[id];
                        if (rev[id]) {
                            ++total;
                        } else {
                            --total;
                        }
                    } else {
                        ++s[id];
                        if (rev[id]) {
                            --total;
                        } else {
                            ++total;
                        }
                    }
                    nums1[x++] ^= 1;  

                }

            } else if (q[0] == 2) {
                v += total * q[1];

            } else {
                r.push_back(v);
            }

        }
        return r;

    }
}; 
