class SegmentTree {
private:
    struct Node {
        int zerosCnt;
        int onesCnt;

        Node operator + (Node& that) {
            return {zerosCnt + that.zerosCnt, onesCnt + that.onesCnt};
        }

        void swapITimes(int i) {
            if (i % 2) swap(zerosCnt, onesCnt);
        }
    };
    vector<Node> tree;
    vector<int> lazy;

    int maxSegmentSz;

    void printTree() {
        cout << "\n";
        for (Node& node : tree) cout<<"{"<<node.zerosCnt << "," << node.onesCnt << "} ";
        cout << "\n";
    }

    int querySegment(int treeIdx, int treeLeft, int treeRight, int qLeft, int qRight) {
        // make it un-lazy
        if (lazy[treeIdx] != 0) {
            tree[treeIdx].swapITimes(lazy[treeIdx]);

            if (treeLeft != treeRight) {
                lazy[2 * treeIdx + 1] += lazy[treeIdx];
                lazy[2 * treeIdx + 2] += lazy[treeIdx];
            }

            lazy[treeIdx] = 0;
        }

        // check if segment don't overlap
        if (treeLeft > qRight || treeRight < qLeft) return 0;

        // if segment completely overlap
        if (qLeft <= treeLeft && treeRight <= qRight) {
            return tree[treeIdx].onesCnt;
        }

        int mid = treeLeft + (treeRight - treeLeft) / 2;

        int QLeftResult = querySegment(2 * treeIdx + 1, treeLeft, mid, qLeft, qRight);
        int QRightResult = querySegment(2 * treeIdx + 2, mid + 1, treeRight, qLeft, qRight);

        return QLeftResult + QRightResult;
    }

    void updateSegment(int treeIdx, int treeLeft, int treeRight, int qLeft, int qRight) {
        // make it un-lazy
        if (lazy[treeIdx] != 0) {
            tree[treeIdx].swapITimes(lazy[treeIdx]);

            if (treeLeft != treeRight) {
                lazy[2 * treeIdx + 1] += lazy[treeIdx];
                lazy[2 * treeIdx + 2] += lazy[treeIdx];
            }

            lazy[treeIdx] = 0;
        }

        // check if segment don't overlap
        if (treeLeft > qRight || treeRight < qLeft) return;

        // if segment completely overlap
        if (qLeft <= treeLeft && treeRight <= qRight) {
            tree[treeIdx].swapITimes(1);

            if (treeLeft != treeRight) {
                lazy[2 * treeIdx + 1]++;
                lazy[2 * treeIdx + 2]++;
            }

            return;
        }

        int mid = treeLeft + (treeRight - treeLeft) / 2;

        updateSegment(2 * treeIdx + 1, treeLeft, mid, qLeft, qRight);
        updateSegment(2 * treeIdx + 2, mid + 1, treeRight, qLeft, qRight);

        tree[treeIdx] = tree[2 * treeIdx + 1] + tree[2 * treeIdx + 2];
    }

    void buildTree(int treeIdx, int left, int right, vector<int>& nums) {
        if (left == right) {
            if (nums[left])
                tree[treeIdx] = {0, 1};
            else
                tree[treeIdx] = {1, 0};

            return;
        }

        int mid = left + (right - left) / 2;

        buildTree(2 * treeIdx + 1, left, mid, nums);
        buildTree(2 * treeIdx + 2, mid + 1, right, nums);

        tree[treeIdx] = tree[2 * treeIdx + 1] + tree[2 * treeIdx + 2];
    }
public:
    SegmentTree (vector<int>& nums) {
        maxSegmentSz = nums.size();

        tree.resize(maxSegmentSz * 4);
        lazy.resize(maxSegmentSz * 4);

        buildTree(0, 0, maxSegmentSz - 1, nums);

        // cout << "\nBuildFinish"; printTree();
    }

    void updateSegment(int qLeft, int qRight) {
        updateSegment(0, 0, maxSegmentSz - 1, qLeft, qRight);
        // cout << "\nupdateFinish"; printTree();
    }

    int querySegment(int qLeft, int qRight) {
        return querySegment(0, 0, maxSegmentSz - 1, qLeft, qRight);
        // cout << "\nQeuryFinish"; printTree();
    }
};
class Solution {
private:
    SegmentTree* segmentTree;
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        segmentTree = new SegmentTree(nums1);

        int sz = nums1.size();

        vector<long long> queryResults;
        long long currResult = 0;

        for (int& num : nums2) currResult += num;

        for (vector<int>& query : queries) {
            if (query[0] == 1) {
                segmentTree->updateSegment(query[1], query[2]);
            } else if (query[0] == 2) {
                currResult += ((long long)query[1]) * segmentTree->querySegment(0, sz-1);
            } else {
                queryResults.push_back(currResult);
            }
        }

        return queryResults;
    }
};







