class Solution {
    public long[] handleQuery(int[] nums1, int[] nums2, int[][] queries) {
        int n=nums1.length;
        ArrayList<Long> list=new ArrayList<>();
        Long sum=0L;
        BitSet bs=new BitSet(n);
        for(int i=0;i<n;i++){
            sum+=1L*nums2[i];
            if(nums1[i]==1)bs.set(i);
        }
        for(int[] q:queries){
            if(q[0]==1){
                bs.flip(q[1],q[2]+1);
            }else if(q[0]==2){
                sum+=1L*q[1]*bs.cardinality();
            }else{
                list.add(sum);
            }
        }
        long[] ans=new long[list.size()];
        int i=0;
        for(long l:list){
            ans[i]=list.get(i);
            i++;
        }
        return ans;
    }
}
