# Code
```
/**
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @param {number[][]} queries
 * @return {number[]}
 */
var handleQuery = function (nums1, nums2, queries) {
  let ans = [];
  let nums1Ones = 0;
  for (let val of nums1) 
    if (val === 1) nums1Ones++;
  let type3Result = nums2.reduce((a, g) => a + g);

  for (let query of queries) {
    let type = query[0];
    switch (type) {
      case 1:
        let [_, l, r] = query;
        let oldOnes = 0;
        let bitSum = r - l + 1;
        for (let i = l; i <= r; i++) {
          if (nums1[i] === 1) oldOnes++;
          nums1[i] = Math.abs(nums1[i] - 1);
        }

        // update value of nums1Ones, code in case 2 will use it
        nums1Ones = nums1Ones + (bitSum - oldOnes - oldOnes);
        break;
      case 2:
        let [x, p, y] = query;
        // we don't really care each item value of nums2
        type3Result += p * nums1Ones;
        break;
      case 3:
        ans.push(type3Result);
        break;
      default:
        break;
    }
  }

  return ans;
};
