**1. Static Segment Tree**
```
class Solution {
 private:
  class SegmentTree {
   public:
    SegmentTree(const vector<int> &nums)
      : n_(static_cast<int>(nums.size())),
        tree_(tree_size(), 0),
        lazy_(tree_.size(), false) {
      build_tree_impl(nums, 0, 0, n_);
    }

    void update(const int left, const int right) {
      update_impl(0, 0, n_, left, right);
    }

    int read() {
      return read_impl(0, 0, n_, 0, n_);
    }

   private:
    int tree_size() const {
      return (1 << static_cast<int>(ceil(log2(n_)) + 1)) - 1;
    }

    void build_tree_impl(const vector<int> &nums, const int node, const int begin, const int end) {
      if (end - begin == 1) {
        tree_[node] = nums[begin];
        return;
      }

      const int left_node = 2 * node + 1;
      const int right_node = 2 * node + 2;
      const int mid = begin + (end - begin) / 2;
      build_tree_impl(nums, left_node, begin, mid);
      build_tree_impl(nums, right_node, mid, end);
      tree_[node] = tree_[left_node] + tree_[right_node];
    }

    void update_impl(const int node, const int begin, const int end, const int left, const int right) {
      if (left >= end || right <= begin) {
        return;
      }

      if (begin >= left && end <= right) {
        const int ones = tree_[node];
        const int zeros = end - begin - ones;
        tree_[node] = zeros;
        lazy_[node] = !lazy_[node];
        return;
      }

      push_down(node, begin, end);

      const int left_node = 2 * node + 1;
      const int right_node = 2 * node + 2;
      const int mid = begin + (end - begin) / 2;

      update_impl(left_node, begin, mid, left, right);
      update_impl(right_node, mid, end, left, right);
      tree_[node] = tree_[left_node] + tree_[right_node];
    }

    int read_impl(const int node, const int begin, const int end, const int left, const int right) {
      if (left >= end || right <= begin) {
        return 0;
      }

      if (begin >= left && end <= right) {
        return tree_[node];
      }

      push_down(node, begin, end);

      const int left_node = 2 * node + 1;
      const int right_node = 2 * node + 2;
      const int mid = begin + (end - begin) / 2;

      return read_impl(left_node, begin, mid, left, right) + read_impl(right_node, mid, end, left, right);
    }

    void push_down(const int node, const int begin, const int end) {
      if (!lazy_[node]) {
        return;
      }

      const int left_node = 2 * node + 1;
      const int right_node = 2 * node + 2;
      const int mid = begin + (end - begin) / 2;

      const int left_ones = tree_[left_node];
      const int left_zeros = mid - begin - left_ones;
      tree_[left_node] = left_zeros;
      lazy_[left_node] = !lazy_[left_node];

      const int right_ones = tree_[right_node];
      const int right_zeros = end - mid - right_ones;
      tree_[right_node] = right_zeros;
      lazy_[right_node] = !lazy_[right_node];

      lazy_[node] = false;
    }

    const int n_;
    vector<int> tree_;
    vector<bool> lazy_;
  };

 public:
  vector<long long> handleQuery(const vector<int> &nums1,
                                const vector<int> &nums2,
                                const vector<vector<int>> &queries) {
    constexpr int type_i = 0;
    constexpr int left_i = 1;
    constexpr int right_i = 2;
    constexpr int p_i = 1;
    constexpr int type_1 = 1;
    constexpr int type_2 = 2;
    SegmentTree segment_tree(nums1);
    vector<long long> ret;
    long long sum = accumulate(nums2.begin(), nums2.end(), 0LL);
    for (const vector<int> &query : queries) {
      const int type = query[type_i];
      if (type == type_1) {
        segment_tree.update(query[left_i], query[right_i] + 1);
      } else if (type == type_2) {
        sum += static_cast<long long>(segment_tree.read()) * query[p_i];
      } else {
