        for (auto & q: queries) {
            int op = q[0];
            if      (op == 3) ans.push_back(sum);
            else if (op == 2) sum += 1LL*t.query(1, 0, n-1, 0, n-1)*q[1];
            else              t.flip(1, 0, n-1, q[1], q[2]);
