class Solution {
    static long seg[], lazy[];

    public long[] handleQuery(int[] nums1, int[] nums2, int[][] queries) {
        seg = new long[nums1.length * 4];
        lazy = new long[nums1.length * 4];
        int n = nums1.length;
        long a[] = new long[nums1.length + 1];
        ArrayList<Long> res = new ArrayList<>();
        for (int i = 1; i <= nums1.length; i++) {
            a[i] = nums1[i - 1];
        }
        build(1, 1, n, a);
        long sum = 0;
        for (int i = 0; i < nums2.length; i++) {
            sum += (long) nums2[i];
        }
        for (int i = 0; i < queries.length; i++) {
            if (queries[i][0] == 1) {
                updateRange(1, 1, n, queries[i][1] + 1, queries[i][2] + 1, 1);
            } else if (queries[i][0] == 2) {
                long s = sumInRange(1, 1, n, 1, n);

                sum += s * (long) queries[i][1];
            } else {
                res.add(sum);
            }
        }
        long ans[] = new long[res.size()];
        for (int i = 0; i < res.size(); i++) {
            ans[i] = res.get(i);
        }
        return ans;
    }

    static void build(int idx, int s, int e, long a[]) {
        if (s == e) {
            seg[idx] = a[s];
            return;
        }
        build(idx * 2, s, (s + e) / 2, a);
        build(idx * 2 + 1, (s + e) / 2 + 1, e, a);
        seg[idx] = seg[idx * 2] + seg[idx * 2 + 1];

    }

    static void process(int idx, int s, int e) {
        if (lazy[idx] % 2 == 1) {
            seg[idx] = (e - s + 1) - (seg[idx]);
        }
        if (s < e) {
            lazy[idx * 2] += lazy[idx];
            lazy[idx * 2 + 1] += lazy[idx];
        }
        lazy[idx] = 0;
    }

    static void updateRange(int idx, int s, int e, int l, int r, long val) {
        process(idx, s, e);
        if ((l > e) || s > r) {
            return;
        }
        if (s >= l && e <= r) {
            lazy[idx] += val;
            process(idx, s, e);
            return;
        }
        updateRange(idx * 2, s, (s + e) / 2, l, r, val);
        updateRange(idx * 2 + 1, (s + e) / 2 + 1, e, l, r, val);
        seg[idx] = (seg[idx * 2] + seg[idx * 2 + 1]);
    }

    static long sumInRange(int idx, int s, int e, int l, int r) {
        process(idx, s, e);
        if ((l > e) || s > r) {
            return 0;
        }
        if (s >= l && e <= r) {
            return seg[idx];
        }
        return (sumInRange(idx * 2, s, (s + e) / 2, l, r) + sumInRange(idx * 2 + 1, (s + e) / 2 + 1, e, l, r));
    }

}
