# Approach
Square Root Decomposition with lazy updates

# Complexity
- Time complexity:
O(q * sqrt(n))

- Space complexity:
O(sqrt(n))

# Code
```
class Solution {
    static class Structure {
        int n;
        int ones;
        int zeroes;
        int numberOfBlocks;
        List<Block> blocks;
        int[] a;

        public Structure(int n, int[] a) {
            this.n = n;
            this.a = a;
            this.ones = 0;
            this.zeroes = 0;
            this.numberOfBlocks = (int)Math.ceil(Math.sqrt((double)n));

            blocks = new ArrayList<>();

            buildBlocks();
        }

        private void buildBlocks() {
            for (int itr = 0; itr < numberOfBlocks; ++itr) {
                blocks.add(new Block(itr, n, a, numberOfBlocks));

                updateOnesAndZeroes(blocks.get(itr), 1);
            }
        }

        public void update(int l, int r) {
            int index = l / numberOfBlocks;
            int current = l;

            while (current <= r) {
                Block block = blocks.get(index);

                updateOnesAndZeroes(block, -1);

                if (block.start == current && block.end <= r) {
                    block.lazyUpdate();
                }
                else {
                    block.realUpdate(current, Math.min(block.end, r));
                }

                updateOnesAndZeroes(block, 1);

                index++;
                current = index * numberOfBlocks;
            }
        }

        private void updateOnesAndZeroes(Block block, int mul) {
                ones = ones + block.ones * mul;
                zeroes = zeroes + block.zeroes * mul;
        }
    }

    static class Block {
        int index;
        int n;
        int size;
        int start;
        int end;
        int ones;
        int zeroes;
        boolean lazy;
        int[] a;

        public Block(int index, int n, int[] a, int size) {
            this.index = index;
            this.n = n;
            this.a = a;
            this.size = Math.min(size, n - (index * size));

            start = index * size;
            end = start + this.size - 1;
            lazy = false;

            buildBlock();
        }

        public void lazyUpdate() {
            swapOnesAndZeroes();
            lazy = !lazy;
        }

        public void realUpdate(int l, int r) {
            if (lazy) {
                lazy = !lazy;
                flipBits(start, end);
            }

            flipBits(l ,r);

            buildBlock();
        }

        private void flipBits(int l, int r) {
            for (int itr = l; itr <= r; ++itr) {
                a[itr] = a[itr] ^ 1;
            }
        }

        private void buildBlock() {
            ones = 0;
            zeroes = 0;

            for (int itr = start; itr <= end; itr++) {
                if (a[itr] == 0) {
                    zeroes++;
                }
                else {
                    ones++;
                }
            }
        }

        private void swapOnesAndZeroes() {
            int temp = ones;
            ones = zeroes;
            zeroes = temp;
        }
    }

    public long[] handleQuery(int[] a, int[] b, int[][] queries) {
        List<Long> ans = new ArrayList<>();
        Structure structure = new Structure(a.length, a);
        long sum = (long)0;

        for (int num : b) {
            sum = sum + num;
        }

        for (int[] query : queries) {
            switch (query[0]) {
                case 1 : structure.update(query[1], query[2]); break;
                case 2 : sum = sum + (long)query[1] * (structure.ones); break;
                case 3 : ans.add(sum);
            }
        }

        return convertToArray(ans);
    }

    private long[] convertToArray(List<Long> al) {
        long result[] = new long[al.size()];

        for (int itr = 0; itr < al.size(); ++itr) {
            result[itr] = al.get(itr);
        }

        return result;
    }
}
