struct node {
    int sum = 0;
    int flip = 0;
    int getsum(int len) {
        if (flip) return len - sum;
        return sum;
    }
};
class tree {
    public:
    int n;
    int c;
    vector<node> dp;
    int base = 1;
    int init(vector<int>& nums1) {
        n = nums1.size();
        c = n * 4 + 1;
        int l = 0;
        int r = n - 1;
        dp.resize(c);
        return build(nums1, l, r, 1);
    }

    int build(vector<int>& nums, int l, int r, int p) {
        if (l == r) {
            dp[p].sum = nums[l];
            dp[p].flip = 0;
            return dp[p].sum;
        }
        int m = (l + r) / 2;
        int ls = build(nums, l, m, p * 2);
        int rs = build(nums, m + 1, r, p * 2 + 1);
        dp[p].sum = ls + rs;
        dp[p].flip = 0;
        return dp[p].sum;
    }
    int getsum() {
        return dp[base].getsum(n);
    }

    int flip(int l, int r, int x, int y, int p, int f) {
        int sum = 0;
        int len = r - l + 1;
        if (x > r || y < l) {
            dp[p].flip = dp[p].flip ^ f;
            return dp[p].getsum(len);
        }
        else if (x <= l && r <= y) {
            dp[p].flip = dp[p].flip ^ f ^ 1;
            return dp[p].getsum(len);
        }
        int m = (l + r) / 2; 

        int ls = flip(l, m, x, y, p * 2, f ^ dp[p].flip);
        int rs = flip(m + 1, r, x, y, p * 2 + 1, f ^ dp[p].flip);
        dp[p].flip = 0;
        dp[p].sum = ls + rs;
        return dp[p].sum;
    }
};
class Solution {
public:
    int n;
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& qs) {
        n = nums1.size();
        vector<long long> ans;
        tree seg;
        seg.init(nums1);
        long long sum = 0;
        for (auto v : nums2) sum += v;
        for (auto& q : qs) {
            if (q[0] == 1) {
                seg.flip(0, n -1, q[1], q[2], 1, 0);
            } else if (q[0] == 2) {
                sum = (long long)seg.getsum() * (long long)q[1] + sum;
            } else if (q[0] == 3) {
                ans.push_back(sum);
            }
        }
        return ans;
    }


};
