      seg[ind].mn = min(seg[2 * ind + 1].mn, seg[2 * ind + 2].mn);
      seg[ind].odd = seg[2 * ind + 1].odd + seg[2 * ind + 2].odd;
      seg[ind].even = seg[2 * ind + 1].even + seg[2 * ind + 2].even; // operation may change to min,max,add,xor
   }
};

