public class Solution {
    const int N = (int)1e5 + 1;
    public class Node{
        public int l, r;
        public long cnt, add;
        public Node(int l, int r){
            this.l = l;
            this.r = r;
            this.add = 0;
        }
    }
    Node[] tr = new Node[N * 4];

    public long[] HandleQuery(int[] nums1, int[] nums2, int[][] queries) {
        Build(1, 1, N);
        for(int i = 0; i < nums1.Length; i++){
            if(nums1[i] == 1)
                Upd(1, i + 1, i + 1);
        }
        List<long> list = new();
        long ans = 0;
        foreach(var n in nums2) ans += n;
        foreach(var q in queries){
            int n = q[0], l = q[1], r = q[2];
            if(n == 1)
                Upd(1, l + 1, r + 1);
            else if(n == 2)
                ans += Qr(1, 1, nums2.Length) * (long)l;
            else
                list.Add(ans);
        }
        return list.ToArray();
    }

    public void Build(int u, int l, int r){
        tr[u] = new Node(l, r);
        if(l == r) return;
        int mid = l + r >> 1;
        Build(u << 1, l, mid);
        Build(u << 1 | 1, mid + 1, r);
    }

    public void PushUp(int u){
        tr[u].cnt = tr[u << 1].cnt + tr[u << 1 | 1].cnt;
    }

    public void PushDown(int u, int l, int r){
        if (tr[u].add == 0) return;
        tr[u << 1].add ^= 1; tr[u << 1 | 1].add ^= 1;
        int mid = (l + r) >> 1;
        tr[u << 1].cnt = (mid - l + 1) - tr[u << 1].cnt;
        tr[u << 1 | 1].cnt = (r - mid) - tr[u << 1 | 1].cnt;
        tr[u].add = 0;
    }

    public void Upd(int u, int l, int r){
        if(tr[u].l >= l && tr[u].r <= r){
            tr[u].cnt = tr[u].r - tr[u].l + 1 - tr[u].cnt;
            tr[u].add ^= 1;
        }
        else{
            PushDown(u, tr[u].l, tr[u].r);
            int mid = tr[u].l + tr[u].r >> 1;
            if(mid >= l) Upd(u << 1, l, r);
            if(mid < r) Upd(u << 1 | 1, l, r);
            PushUp(u);
        }
    }

    public long Qr(int u, int l, int r){
        if(tr[u].l >= l && tr[u].r <= r)
            return tr[u].cnt;
        int mid = tr[u].l + tr[u].r >> 1;
        long ans = 0;
        if(mid >= l) ans = Qr(u << 1, l, r);
        if(mid < r) ans += Qr(u << 1 | 1, l, r);
        return ans;
    }
}
