class Solution {
public:
    vector<long long> tree, A, lazy;
    void build(long long node, long long start, long long end) {
        if (start == end) {
            tree[node] = A[start];
        } else {
            long long mid = (start + end) / 2;
            build(2 * node, start, mid);
            build(2 * node + 1, mid + 1, end);
            tree[node] = tree[2 * node] + tree[2 * node + 1];
        }
    }

    void updateRange(long long node, long long start, long long end, long long l, long long r) {
        if (lazy[node] != 0) {
            tree[node] = (end - start + 1) - tree[node];
            if (start != end) {
                lazy[node * 2] = !lazy[node * 2];
                lazy[node * 2 + 1] = !lazy[node * 2 + 1];
            }
            lazy[node] = 0;
        }
        if (start > end or start > r or end < l)
            return;
        if (start >= l and end <= r) {
            tree[node] = (end - start + 1) - tree[node];
            if (start != end) {
                lazy[node * 2] = !lazy[node * 2];
                lazy[node * 2 + 1] = !lazy[node * 2 + 1];
            }
            return;
        }
        long long mid = (start + end) / 2;
        updateRange(node * 2, start, mid, l, r);
        updateRange(node * 2 + 1, mid + 1, end, l, r);
        tree[node] = tree[node * 2] + tree[node * 2 + 1];
    }

    long long queryRange(long long node, long long start, long long end, long long l, long long r) {
        if (start > end or start > r or end < l)
            return 0;
        if (lazy[node] != 0) {
            tree[node] += (end - start + 1) * lazy[node];
            if (start != end) {
                lazy[node * 2] += lazy[node];
                lazy[node * 2 + 1] += lazy[node];
            }
            lazy[node] = 0;
        }
        if (start >= l and end <= r)
            return tree[node];
        long long mid = (start + end) / 2;
        long long p1 = queryRange(node * 2, start, mid, l, r);
        long long p2 = queryRange(node * 2 + 1, mid + 1, end, l, r);
        return (p1 + p2);
    }

    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        int n = nums1.size();
        A.resize(n + 1);
        tree.resize(5e5);
        lazy.resize(5e5);
        for(auto &x: lazy){
            x = 0;
        }
        for(int x = 1; x <= n; x++){
            A[x] = nums1[x - 1] * 1LL;
        }
        build(1, 1, n);
        long long sum = 0;
        for(auto x: nums2){
            sum += x * 1LL;
        }
        vector<long long> res;
        for(int x = 0; x < queries.size(); x++){
            if(queries[x][0] == 1){
                updateRange(1, 1, n, queries[x][1] + 1, queries[x][2] + 1);
            }else if(queries[x][0] == 2){
                long long off = queryRange(1, 1, n, 1, n);
                off = off * (long long)queries[x][1];
                sum += off;                
            }else{
                res.push_back(sum);                
            }
        }
        return res;        
    }
};
