class Solution {
    public long[] handleQuery(int[] nums1, int[] nums2, int[][] queries) {
        long curSum = 0L;
        for (int i = 0; i < nums2.length; i++) {
            curSum += nums2[i];
        }

        SegTree tree = new SegTree(nums1);

        int resultSize = 0;
        for(int i = 0; i < queries.length; i++) {
            if (queries[i][0] == 3) resultSize++;
        }

        long[] result = new long[resultSize];

        int pos = 0;
        for (int[] q : queries){
            if (q[0] == 1) {
                tree.update(q[1], q[2]);
            }
            if (q[0] == 2) {
                curSum += tree.query(0, nums1.length - 1) * q[1];
            }
            if (q[0] == 3) {
                result[pos++] = curSum;
            }
        }

        return result;
    }

    private static class SegTree{

        long[] tree;
        boolean[] flip;
        int size;

        public SegTree(int nums[]) {
            int s = 1;
            while (s < nums.length) s <<= 1;
            size = s;
            tree = new long[s * 2];
            flip = new boolean[s * 2];

            for (int i = 0; i < nums.length; i++) {
                tree[i + size] = nums[i];
            }
            for (int i = size - 1; i > 0; i--) {
                tree[i] = tree[i * 2] + tree[i * 2 + 1];
            }
        }

        public long query(int start, int end) {
            return query(start, end, 0, size - 1, 1);
        }

        public long query(int start, int end, int low, int high, int cur) {
            if (flip[cur]) {
                tree[cur] = high - low + 1 - tree[cur];
                if (low != high) {
                    flip[cur * 2] = !flip[cur * 2];
                    flip[cur * 2 + 1] = !flip[cur * 2 + 1];
                }
                flip[cur] = false;
            }
            if (start > high || end < low)
                return 0L;
            if (start <= low && end >= high) {
                return tree[cur];
            }

            int mid = low + (high - low) / 2;
            return query(start, end, low, mid, cur * 2) + query(start, end, mid + 1, high, cur * 2 + 1);
        }

        public void update(int start, int end) {
            update(start, end, 0, size - 1, 1);
        }

        public void update(int start, int end, int low, int high, int cur) {
            if (flip[cur]) {
                tree[cur] = high - low + 1 - tree[cur];
                if (low != high) {
                    flip[cur * 2] = !flip[cur * 2];
                    flip[cur * 2 + 1] = !flip[cur * 2 + 1];
                }
                flip[cur] = false;
            }

            if (start > high || end < low) return;
            if (start <= low && end >= high) {
                tree[cur] = high - low + 1 - tree[cur];
                if (low != high) {
                    flip[cur * 2] = !flip[cur * 2];
                    flip[cur * 2 + 1] = !flip[cur * 2 + 1];
                }
                return;
            }

            int mid = low + (high - low) / 2;
            update(start, end, low, mid, cur * 2);
            update(start, end, mid + 1, high, cur * 2 + 1);
            tree[cur] = tree[cur * 2] + tree[cur * 2 + 1];
        }

    }
}
