# Approach
Use segment trees with lazy update to flip values of nums1 array. Using this we can easily keep track of ones present in nums1 array.

# Complexity
- Time complexity:O(n)

- Space complexity:O(n)

# Code
```
class Solution {
public:
    vector<pair<int,int>>tree;
    vector<int>lazy;
    void lazy_update(int s,int e,int i)
    {
        if(lazy[i]>0)
        {
            swap(tree[i].first,tree[i].second);
            if(s!=e)
            {
                lazy[2*i+1]=(lazy[2*i+1]+1)%2;
                lazy[2*i+2]=(lazy[2*i+2]+1)%2;
            }
            lazy[i]=(lazy[i]+1)%2;
        }
    }
    void build(int s,int e,int i,vector<int>&v)
    {
        if(s==e)
        {
            if(v[s]==0)
            {
                tree[i].first++;
            }else
            {
                tree[i].second++;
            }
            return;
        }
        int mid=(s+e)>>1;
        build(s,mid,2*i+1,v);
        build(mid+1,e,2*i+2,v);
        tree[i].first=tree[2*i+1].first+tree[2*i+2].first;
        tree[i].second=tree[2*i+1].second+tree[2*i+2].second;
        return;
    }
    void update(int s,int e,int i,int l,int r)
    {
        lazy_update(s,e,i);
        if(s>r || e<l)
        {
            return;
        }
        if(l<=s and e<=r)
        {
            lazy[i]=(lazy[i]+1)%2;
            lazy_update(s,e,i);
            return;
        }
        int mid=(s+e)/2;
        update(s,mid,2*i+1,l,r);
        update(mid+1,e,2*i+2,l,r);
        tree[i].first=tree[2*i+1].first+tree[2*i+2].first;
        tree[i].second=tree[2*i+1].second+tree[2*i+2].second;
        return;
    }
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
       int n=nums1.size();
       tree=vector<pair<int,int>>(4*n);
       lazy.assign(4*n,0);
       long long ans=0;
        for(auto &itr:nums2)
        {
            ans+=itr;
        }
        build(0,n-1,0,nums1);
        vector<long long>res;
       for(auto itr:queries)
       {
           int type=itr[0];
           if(type==1)
           {
               int l=itr[1],r=itr[2];
               update(0,n-1,0,l,r);
           }
           else if(type==2)
           {
               long long p=itr[1];
               ans+=(1LL*tree[0].second)*p;
           }else
           {
               res.push_back(ans);
           }
       }
        return res;
    }
};
