    private int findStartPos(List<Interval> intervals, int value) {
        int l = 0, r = intervals.size() - 1;
        while (l <= r) {
            int m = (l + r) >> 1;
            if (intervals.get(m).start == value) return m;
            else if (intervals.get(m).start < value) l = m + 1;
            else r = m - 1;
