class Solution {
public:
    vector<Interval> insert(vector<Interval>& a, Interval newitv) {
        vector<Interval> res;
        int i = 0;
        for (; i < a.size() && a[i].end < newitv.start; i++) res.push_back(a[i]);

        for (; i < a.size() && max(a[i].start, newitv.start) <= min(a[i].end, newitv.end); i++) {
            newitv.start = min(a[i].start, newitv.start);
            newitv.end = max(a[i].end, newitv.end);
        }
        res.push_back(newitv);

        for (; i < a.size(); i++) res.push_back(a[i]);
        return res;
    }
};
```

