class Solution {
public:
    // merge intervals.
    vector<vector<int>> merge(vector<vector<int>>&ans,int n)
    {
        vector<vector<int>>res;
        res.push_back(ans[0]);
        int prev=res[0][1];
        for(int i=1;i<=n;i++)
        {
            if(ans[i][0]<=prev)
            {
                res[res.size()-1][1]=max(res[res.size()-1][1],ans[i][1]);
                prev=res[res.size()-1][1];
            }
            else
            {
                res.push_back(ans[i]);
                prev=ans[i][1];
            }
        }
        return res;
    }
    vector<vector<int>> insert(vector<vector<int>>& intervals, vector<int>& newInterval) {
        int n=intervals.size();
        // Adding interval 
        vector<vector<int>>ans;
        bool first=false;
        int i=0;
        while(i<n)
        {
            if(!first && intervals[i][0]>=newInterval[0])
            {
                ans.push_back(newInterval);
                first=true;
            }
            else
            {
                ans.push_back(intervals[i]);
                i++;
            }
        }
        if(!first)
            ans.push_back(newInterval);

        // merge intervals
        return merge(ans,n);
    }
};
