var insert = function(intervals, newInterval) {
    const size = intervals.length;
    const [start, end] = newInterval;

    const overlap = (interval) => {
        const [first, last] = interval;
        //3...5    <-first, last
        //  4....8 <-start, end
        if(start <= last &&  end >= last) return true;
        //1...5 <-first, last
        // 2.3  <-start, end
        return (first <= end && last >= end);
    }

    const overlaps = intervals.map(overlap);
    const result = [];

	//push left
