    let max = end;
    while(i < size && overlaps[i]) {
        min = Math.min(min, intervals[i][0]);
        max = Math.max(max, intervals[i][1]);
        i++;
    }

    result.push([min, max]);

	//push right
    while(i < size) {
        result.push(intervals[i++]);
    }

    return result;
};
