private void backTracking(TreeNode node, List<String> collector, StringBuilder sb) {
	int len = sb.length();
	sb.append(node.val);
	if (node.left == null && node.right == null) {
		collector.add(sb.toString());
	} else {
		sb.append("->");
		if (node.left != null) backTracking(node.left, collector, sb);
		if (node.right != null) backTracking(node.right, collector, sb);
	}
	sb.setLength(len);
}
