        if (root == null) return;
        oneResult.Add($"{root.val}");
        if (root.left == null && root.right == null) {
            // leaf
            result.Add(string.Join("->", oneResult));
        } else {
            DFS(root.left, oneResult, result);
            DFS(root.right, oneResult, result);
        }
        oneResult.RemoveAt(oneResult.Count - 1);
    }
}
