    vector<string> binaryTreePaths(TreeNode* root) {
        if (!root)
            return {};
        if (!root->left && !root->right)
            return {to_string(root->val)};

        vector<string> output;
        for (auto node : binaryTreePaths(root->left))
            output.push_back(to_string(root->val) + "->" + node);
        for (auto node : binaryTreePaths(root->right))
            output.push_back(to_string(root->val) + "->" + node);

        return output;
    }
};
