class Solution:
    def binaryTreePaths(self, root: TreeNode) -> List[str]:
        #backtracking
        path,values = [], []
        if not root:
            return path
        self.backtracking(root, values, path)
        return path

    def isLeaf(self, node):    #determine if leaf node
        return not node.left and not node.right

    def buildPath(self, values):
        string = ''
        for i in range(len(values)):
            string += str(values[i])
            if i != len(values)-1:
                string += '->'    #add -> beteween 2 numbers
        return string

    def backtracking(self, node, values, path):
        if not node:
            return 
        values.append(node.val)
        if self.isLeaf(node):
            path.append(self.buildPath(values))     #find a path, add it to the answer
        else:
            self.backtracking(node.left, values, path)    #goto left  leaf
            self.backtracking(node.right, values, path)  #goto right leaf
        values.pop()    #backtrack to the origin to find another solution
```

