    def binaryTreePaths(self, root: TreeNode) -> List[str]:
        if not root:
            return []

        paths, start = [str(root.val)], 0
        layer = [root]  # queue

        while layer:
            root = layer.pop(0)
            if root.left:
                paths.append(paths[start] + '->' + str(root.left.val))
                layer.append(root.left)
            if root.right:
                paths.append(paths[start] + '->' +  str(root.right.val))
                layer.append(root.right)
            if not root.left and not root.right:
                start += 1
            else:
                paths.pop(start)

        return paths
