	 * };
	 */
	class Solution {
	public:
		bool isleaf(TreeNode* root){
			return (!root->left && !root->right);
		}
		void chk(vector<vector<int>>&v, vector<int>&a, TreeNode* root){
			if(!root){return;}
			a.push_back(root->val);
			if(isleaf(root)){v.push_back(a);}
			chk(v, a, root->left);
			chk(v, a, root->right);
			a.pop_back();
