class Solution {
public:
    int distMoney(int money, int children) {
        if(money<children) return -1;
        for(int i =0;i<children;i++){
            money--;
        }
        int count = 0;
        int i;
        for( i =0;i<children;i++){
            if(money>=7){
                money-=7;
            }
            else break;
        }
        if(i==children && money!=0) return count-1;
        if(children -i ==1 && money == 3) return count-1;
        return count;
    }
};
```

