class Solution {
    public int distMoney(int money, int children) {
        int maxEightDollars = Math.min((money - children) / 7, children);

        for (int hk = maxEightDollars; hk >= 0; hk--) {
            int rem = money - children - hk * 7;
            int remainingChildren = children - hk;

            if (remainingChildren == 0 && rem == 0) {
                return hk;
            } else if (remainingChildren == 1) {
                if (rem != 3) { 
                    return hk;
                }
            } else if (remainingChildren > 1 && rem >= 0) {
                return hk;
            }
        }

        return -1;
    }
}

