class Solution {

    /**
     * @param Integer $money
     * @param Integer $children
     * @return Integer
     */
    function distMoney($money, $children) {
        $min = 1;
        $must = 8;
        $deny = 4;

        $stop = $deny - $min;
        $best = $must - $min;

        if ($money - $children < 0) {
            return -1;
        }

        $list = array_fill(0, $children, $min);
        $money -= $children * $min;

        foreach ($list as $index => &$salary) {
            if ($money == 0) {
                break;
            }

            $inc = min($best, $money);

            if ($inc == $stop) {
                $inc--;
            }

            $money -= $inc;

            $salary += $inc;
        }

        $list[0] += $money;

        return array_count_values($list)[8] ?? 0;
    }
}
```

