class Solution {
    func distMoney(_ money: Int, _ children: Int) -> Int {
        guard money >= children  else { return -1 }
        switch ((money - children).quotientAndRemainder(dividingBy: 7)) {
            case (children..., 1...), ((children+1)..., 0): return children - 1
            case let (max8, 3) where children - max8 < 2: return max8 - 1
            case let (max8, _): return max8
        }
    }
}
