    public int distMoney(int money, int children) {
        if(money<0 && children<0) {
            return -1;
        }
        if(children<=money/8 && money%8!=0) {
            return children-1;
        }
        if(children<money/8 && money%8==0) {
            return children-1;
        }
        if(money<8) {
            if(children<=money) {
                if(money==4 && children==1) {
                    count--;
                    money=0;
                    children--;
                }
                else {
                    money=0;
                    children=0;
                }
            }
            else {
                children -= money;
                money=0;
                while(children>0) {
                    money += 7;
                    count--;
                    children -= money;
                    money = 0;
                }
                children=0;
                money=0;
            }
        }
        else {
            count++;
            return distMoney(money-8, children-1);
        }

        if(count<-1) {
            return -1;
        }
        return count;
    }
}
