class Solution {
    public int distMoney(int money, int children) 
    {
        int eightDollarChild = 0,i=0;
        int child[] = new int[children]; 
        Arrays.fill(child,1); 
        money -= children;

        // case when money == children
        if(money == 0) 
            return eightDollarChild;
        // case when money < childern 
        if(money < 0)
            return -1; 

        for(i=0; i<child.length && money >=7; i++)
        {       
            child[i] += 7; 
            money -= 7;
            eightDollarChild++; 
        } 
        if(money == 3 && i == child.length - 1)
