            //case when last child will get 4 dollar 
            //so we need to change distribution 
            //of 2nd last child
            eightDollarChild--; 
        }
        if(money > 0 && i == child.length)
        {
            // case when money > children * 8 
            //so last child will get extra money 
            eightDollarChild--; 
        }
        return eightDollarChild; 
    }
}
