class Solution:
    def distMoney(self, money: int, children: int) -> int:
        if money / children == 8:
            return children
        if money < children:
            return -1
        counter = 0
        while money > 0 and children > 1:
            if money < (8 + (children - 1)):
                break
            counter += 1
            children -= 1
            money -= 8
            if money == 4 and children == 1:
                counter -= 1
                break
        return counter
