class Solution {
public:
    int distMoney(int money, int children) {
        // case 1: insufficient money 
        // eg : money: 8 children 9
        if(children>money){
            return -1;
        }


        int eightChildren = money/8;

        // case 2
        // eg money 50 children 2 split: (8,42)  
        if(eightChildren>children){
            return children-1;
        }
        int leftMoney = money - eightChildren*8;
        int leftChildren = children - eightChildren;

        // case 3 
        // eg money 12 children 2 split: (9,3)
        if(leftChildren==1 and leftMoney==4){
            if(eightChildren >= 1)
                return eightChildren -1;
            return eightChildren;
        }
        // case 4 
        // money 17 children 2 split: (8,9)
        if(eightChildren == children and leftMoney>0){
            return eightChildren - 1;
        }


        // case 5
        // eg money 21 children 3 split: (8,8,5)
        if(leftChildren==0 or leftChildren<=leftMoney or eightChildren==0)
            return eightChildren;

        //case 6
        // eg 16 children 3 split: (8,7,1)
        return eightChildren - ((leftChildren-leftMoney)/7 + ((leftChildren-leftMoney)%7!=0));


    }
};
```

