class Solution {
    public int distMoney(int money, int children) {

        money -= children;
        if (money < 0) {
            return -1;
        } else if (money < 7) {
            return 0;
        }

        int maxChildrenWhoCanGet = money/7;
        int moneyLeft = money%7;
        int childrenLeft = children-maxChildrenWhoCanGet;

        if (childrenLeft < 0) {
            return children-1;
        }
        else if (moneyLeft == 3 && childrenLeft == 1) {
            return maxChildrenWhoCanGet - 1;
        } 
        else if (moneyLeft > 0 && childrenLeft == 0) {
            return maxChildrenWhoCanGet - 1;
        }
        return maxChildrenWhoCanGet;

    }
}
