# Code
```
class Solution {
public:
    bool check(int money , int lc, int c){
        if(money < 8*lc){
            return false;
        }
        money -= 8*lc;
        c = c - lc;
        if(money < c){
            return false;
        }
        if(money == 4 && c == 1){
            return false;
        }
        if(c == 0 && money>0){
            return false;
        }
        return true;
    }
    int distMoney(int money, int children) {
        int start = 0 , end = children;
        int ans = -1;
        while(start <= end){
            int mid = start + (end-start)/2;
            if(check(money, mid, children)){
                ans = mid;
                start = mid+1;
            }else{
                end = mid-1;
            }
        }
        return ans;
    }
};
```

