  if (money === 8 * children) return children;

  if (children == 2) {
    if (money == 12) return 0;
    if (money < 9) return 0; 
    return 1;
  }

  if (money < 8 + children - 1) return 0;
  return 1 + distMoney(money-8, children-1);
};
