class Solution {
  public int findSmallestInteger(int[] nums, int value) {
    var n = nums.length;
    var map = new int[value][2];

    for (var i=0; i < value; i++)
      map[i][0] = i;

    for (var a : nums) {
      if (a < 0 && a % value != 0)
        map[value + a % value][1]++;
      else
        map[a % value][1]++;
    }
    Arrays.sort(map, (a,b) -> a[1] == b[1] ? Integer.compare(a[0], b[0]) : Integer.compare(a[1], b[1]));

    return map[0][0] + map[0][1] * value;
  }
}
