class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int v) {
        int n=nums.size();
        unordered_map<int,int> mp;
        for(int i=0;i<n;i++){
            mp[((nums[i]%v)+v)%v]++;

        }
        for(int i=0;i<2*n;i++){
            if(mp[i%v]>0){
                mp[i%v]--;
            }
            else return i;
        }
        return n;

    }
};
```

