class Solution {
public:
    int findSmallestInteger(vector<int>& arr, int val) {
        int n = arr.size();
        vector<int> v(n+1,0);
        if(val==1) return n;
        for(int i = 0;i<n;i++)
        {
            if(arr[i]<0 && arr[i]%val!=0)
            arr[i] = val+arr[i]%val;
            else arr[i] = arr[i]%val;
        }
        for(int i = 0;i<n;i++)
        {
           if(arr[i]<=n && v[arr[i]]==0)
           v[arr[i]] = 1; 
           else if(arr[i]<=n)
           {
               int j = arr[i];
               while(j<=n && v[j]==1) j+=val;
               if(j<=n && v[j]==0) v[j] = 1;
           }
        }
        for(int i = 0;i<=n;i++)
        if(v[i]==0) return i;
        return n+1;     
    }
};
